mapper.select:
SELECT member_id, EMAIL, password, NICKNAME
FROM MEMBER
WHERE email = #{email}

SELECT member_id
FROM member
WHERE email = #{email}

mapper.update:
UPDATE MEMBER
SET password = #{password}, oldPassword = #{oldPassword}, editdate = sysdate
WHERE member_id = #{memberId}

mapper.select:
SELECT CASE WHEN COUNT(*) > 0 THEN 1 ELSE 0 END
FROM member
WHERE email = #{email}

SELECT CASE WHEN COUNT(*) > 0 THEN 1 ELSE 0 END
FROM member
WHERE nickname = #{nickname}
